CREATE TABLE IF NOT EXISTS `#__fm_clients` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `userid` int(11) NOT NULL,  `title` varchar(4) NOT NULL default '',  `cell` int(10) NOT NULL default 0,  `phone` int(10) NOT NULL default 0,  `fax` int(10) NOT NULL default 0,  `address` text NOT NULL default '',  `ts` timestamp NOT NULL default CURRENT_TIMESTAMP,   PRIMARY KEY  (`id`),   KEY `#__users` (`userid`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__fm_types` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `label` varchar(40) NOT NULL,   PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__fm_uploads` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `clientid` int(11) NOT NULL,  `typeid` int(11) NOT NULL,  `filename` varchar(40) NOT NULL,  `ext` varchar(4) NOT NULL,  `ts` timestamp NOT NULL default CURRENT_TIMESTAMP,   PRIMARY KEY  (`id`),   KEY `#__fm_clients` (`clientid`),   KEY `#__fm_types` (`typeid`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;INSERT INTO `#__fm_types` (`id`, `label`) VALUES(1, 'Correspondence'),(2, 'Investment File'),(3, 'Documents'),(5, 'Newsletter');INSERT INTO `#__fm_clients` (`id`, `userid`, `title`, `cell`, `phone`, `fax`, `address`, `subscribe`, `ts`) VALUES(1, 59, 'Mr', 724014055, 312665355, 312665355, '25 Galway Rd\r\nBonela\r\nDurban', 0, '2013-04-17 07:55:57'),(2, 61, 'Miss', 724014072, 317646480, 317646480, '23 Galway Road\r\nMayville \r\nDurban', 0, '2013-04-17 11:32:57'),(3, 62, 'Mr', 724014072, 312073946, 312073946, '', 0, '2013-04-17 13:53:22'),(4, 63, 'Mr', 724014072, 312073946, 312073946, '12 Galway Road\r\nBonela\r\nDurban', 0, '2013-04-17 13:55:39'),(5, 64, 'Mr', 724014072, 317646480, 912333445, '12 Galway Road\r\nBonela\r\nDurban', 0, '2013-04-17 13:56:13'),(6, 65, 'Miss', 788776665, 317014455, 2147483647, '12 Galway Road\r\nBonela\r\nDurban', 0, '2013-04-17 13:56:43'),(7, 66, 'Dr', 788776665, 313049